	select NUM,name,id,item,count,price
	from ORDER_VIEW
	WHERE id=#{id} and NUM=#{NUM} and name=#{name}
	ORDER BY NUM



		SELECT sum(price*count)
		FROM ORDER_VIEW
		WHERE id=#{id}



		DELETE FROM ORDER_VIEW
		WHERE NUM=#{NUM}




		DELETE FROM ORDER_VIEW 
		WHERE id=#{id}




		UPDATE ORDER_VIEW
		SET count=#{count}
		WHERE id=#{id} and name=#{name}



	SELECT count(*)
	FROM ORDER_VIEW
	WHERE id=#{id}
	AND name=#{name}



	UPDATE ORDER_VIEW
	SET count= count+#{count}
	WHERE id=#{id}
	AND name=#{name}
